/*
 * Modulos do proprio nucleo do Node:
 * net - Módulo de iteração com o diversos protocolos tcp.
 * Deste modo é possivel controlar a transmissão de dados
 */
var net = require('net');
var server = net.createServer(function(connection){
    console.log('client connected');
    connection.on('end',function(){
        console.log('cliente disconnected');
    });
    connection.write('Hello World!');
    // Ler dados de fluxo editaveis e guardar.
    connection.pipe(connection);
});
server.listen(8080, function(){
    console.log('server is listening');
});