var net = require('net');
// net.connect - Conectar na porta xxx do servidor, array assoc. {key:value}
var client = net.connect({port: 8080},function(){
    console.log('connected to server');
});
client.on('data',data => {
    console.log('Enviado do Server -> '+data.toString());
    client.end();
});
client.on('end',function(){
    console.log('disconnected from the server');
});