class Baralho
{
    cartas = [];

    constructor()
    {
        for(var i of [Paus, Ouros, Copas, Espadas])
        {
            for(var j = 1; j <= 13; j++)
            {
                this.cartas.push(new i(j));
            }
        }
    }

    embaralhar(x)
    {
        for(var i = 0; i <= x; i++ )
        {
            var r1 = Math.floor(Math.random()*52);
            var r2 = Math.floor(Math.random()*52);
            [this.cartas[r1], this.cartas[r2]] = [this.cartas[r2], this.cartas[r1]];
        }
    }
}
module.exports.Baralho = Baralho;

class Carta
{
    cor;
    naipe;
    valor;
    constructor(i)
    {
        this.valor = i;
    }
}

class Paus extends Carta
{
    constructor(i)
    {
        super(i);
        this.cor = "black";
        this.naipe = "paus";
    }
}

class Ouros extends Carta
{
    constructor(i)
    {
        super(i);
        this.cor = "red";
        this.naipe = "ouros";
    }
}

class Copas extends Carta
{
    constructor(i)
    {
        super(i);
        this.cor = "red";
        this.naipe = "copas";
    }
}

class Espadas extends Carta
{
    constructor(i)
    {
        super(i);
        this.cor = "black";
        this.naipe = "espadas";
    }
}